document.querySelector(".hamburger").addEventListener("click",()=>document.querySelector(".nav-links").classList.toggle("fold"));const e="/XPilot",n=document.querySelector(".slide"),o=document.querySelectorAll(".lang-switch span");document.querySelector(".lang-switch").addEventListener("click",()=>{n.classList.toggle("slide-move"),o.forEach(t=>t.classList.toggle("active")),setTimeout(()=>{window.location.pathname.includes("en")?window.location.pathname=window.location.pathname.replace("/en",""):window.location.pathname=window.location.pathname.replace(e,`${e}/en`)},300)});
